mapper.select:

	select * from (select rownum rn, b.*
                from (select * from job order by seq desc) b )
  where rn >= 1 and rn <= 16


  	select * from (select rownum rn, b.*
	                  from (select * from job where seq < #{seq} order by seq desc) b )
	    where rn >= 1 and rn <= 16


	select * from (select rownum rn, b.*
                from (select * from job where seq < #{seq} order by seq desc) b )
  where rn >= 1 and rn <= 16

select * from (select rownum rn, b.* from (select * from job where position in
#{jobsort}
order by seq desc) b ) where rn >= 1 and rn <= 16

select * from (select rownum rn, b.* from (select * from job where seq < #{seq} and position in
#{jobsort}
order by seq desc) b ) where rn >= 1 and rn <= 16
