mapper.select:

	select * from (select rownum rn, b.* 
                from (select * from job ) b order by seq desc)
  where rn >= 1 and rn <= 16 order by seq desc


	select * from (select rownum rn, b.* 
                from (select * from job ) b)
  where rn >= #{startNum} and rn <= #{endNum} order by seq desc
